mapper.update:
update message set status=#{status}
where id in

    #{id}

mapper.select:
select 
from message
where id in(
    select max(id) from message
    where status!=2
    and from_id=1
    and to_id=#{userId}
    and conversation_id=#{topic}
)

select count(id) from message
where status!=2
  and from_id=1
  and to_id=#{userId}
  and conversation_id=#{topic}

select count(id) from message
where status=0
  and from_id=1
  and to_id=#{userId}

      and conversation_id=#{topic}

select 
from message
where status!=2
and from_id=1
and to_id=#{userId}
and conversation_id=#{topic}
order by create_time desc
limit #{offset},#{limit}
